import styled from "styled-components";
import Button from "../UI/Button";

const NewFormArea = styled.div`
    width: 100%;
    margin: 0.5rem 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border: 1px solid black;

    &label {
        display: block;
        font-weight: bold;
        margin-top: 1.5rem;
    }

    &input {
        display: block;
        margin: 2rem;
        width: 100%;
        border: 1px solid black;
        background-color: #ccc;
    }
`

const NewFormInput = () => {
 return (
    <form>
        <NewFormArea>
                <label>Username</label>
                <input type="text" onChange={userNameHandler}/>

                <label>Age (Years)</label>
                <input type="text"onChange={userAgeHandler}/>

            <Button />
        </NewFormArea>
    </form>
 )
}

export default NewFormInput;
