import styled from "styled-components";
import Button from "../UI/Button";
import { useState } from 'react'

const NewFormArea = styled.div`
    width: 100%;
    margin: 0.5rem 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border: 1px solid black;

    &label {
        display: block;
        font-weight: bold;
        margin-top: 1.5rem;
    }

    &input {
        display: block;
        margin: 2rem;
        width: 100%;
        border: 1px solid black;
        background-color: #ccc;
    }
`



const NewFormInput = (props) => {
    const [nameValue, setUserValue] = useState('');
    const [ageValue, setAgeValue] = useState('');
    // const [addUser, setAddUser] = useState([]);


    const userNameHandler = (event) => {
        setUserValue(event.target.value)
        console.log(event.target.value)
    }

    const userAgeHandler = (event) => {
        setAgeValue(event.target.value)
        console.log(event.target.value)

    }

    const submitHandler = (event) => {
        // 버튼 누를 때마다 새로고침 되는거 방지
        event.preventDefault();
        // setAddUser([{UserName: nameValue, Age: ageValue}]);
        props.onAddUser(nameValue,ageValue);



    }


 return (
    <form onSubmit={submitHandler}>
        <NewFormArea>
                <label>Username</label>
                <input type="text" onChange={userNameHandler}/>

                <label>Age (Years)</label>
                <input type="text"onChange={userAgeHandler}/>

            <Button >Add User</Button>
        </NewFormArea>
    </form>
 )
}

export default NewFormInput;
