import React, { useState } from "react";
import classes from "./AddMemo.module.css";
import Card from "../UI/Card";


const AddMemo = (props) => {
    const [ enteredDate, setEnteredDate ] = useState('');


    const enteredDateHandler = (event) => {
        setEnteredDate(event.target.value);
        console.log(event.target.value);
    }

    return (
        <Card className={classes.input}>
            <form>
                <label htmlFor="date">Date</label>
                <input type="date" id="date" onChange={enteredDateHandler}/>
                <label htmlFor="content">To-do</label>
                <input type="text" id="content" placeholder="Enter your To-do"/>
            </form>
        </Card>
    )
}

export default AddMemo;
